        firstName: '‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•',
        lastName: '‡∏£‡∏∞‡∏ö‡∏ö',
        email: 'admin@in2it.co.th',
        phone: '02-123-4567',
        position: 'System Administrator',
        profilePicture: '',
        address: '',
        socialMedia: '',
        lineUserId: '',
        gender: 'MALE',
        leaveBalances: {
          accumulated: 0,
