import { MailService } from '@sendgrid/mail';

// Define interfaces for email service (avoiding client-side imports)
interface UserData {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  title: string;
}

interface LeaveRequest {
  id: string;
  userId: string;
  leaveType: string;
  startDate: Date;
  endDate: Date;
  totalDays: number;
  reason: string;
