enum LeaveStatus {
  PENDING = "‡∏£‡∏≠‡∏û‡∏¥‡∏à‡∏≤‡∏£‡∏ì‡∏≤",
  APPROVED = "‡∏≠‡∏ô‡∏∏‡∏°‡∏±‡∏ï‡∏¥",
  REJECTED = "‡∏õ‡∏è‡∏¥‡πÄ‡∏™‡∏ò"
}

if (!process.env.SENDGRID_API_KEY) {
  throw new Error("SENDGRID_API_KEY environment variable must be set");
}

const mailService = new MailService();
mailService.setApiKey(process.env.SENDGRID_API_KEY);

interface EmailParams {
  to: string;
  from: string;
  subject: string;
  text?: string;
  html?: string;
}

export async function sendEmail(params: EmailParams): Promise<boolean> {
  try {
    if (!process.env.SENDGRID_API_KEY) {
      console.log('SendGrid API key not configured, skipping email');
      return false;
    }

    await mailService.send({
      to: params.to,
      from: params.from,
      subject: params.subject,
      text: params.text || '',
      html: params.html,
    });
    return true;
  } catch (error: unknown) {
    console.error('SendGrid email error:', error);
    // Log the specific error for debugging
    if (error && typeof error === 'object' && 'response' in error) {
      const sendGridError = error as { response?: { body?: unknown } };
      if (sendGridError.response?.body) {
        console.error('SendGrid error details:', sendGridError.response.body);
      }
    }
    return false;
  }
}

