import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { UserData, LeaveRequest, LeaveStatus } from '@/types';
import { leaveRequestsAPI, usersAPI } from '@/services/api';
import { Header } from '@/components/layout/Header';
import { EmployeeCard } from './EmployeeCard';
import { AllLeaveRequests } from './AllLeaveRequests';
import { EmployeeLeaveView } from './EmployeeLeaveView';
import { CalendarView } from '../shared/CalendarView';
import { LeaveRequestModal } from '../shared/LeaveRequestModal';
import { Card, CardContent } from '@/components/ui/card';
import { Loading } from '@/components/ui/loading';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

export const AdminDashboard: React.FC = () => {
  const [, setLocation] = useLocation();
  const [employees, setEmployees] = useState<UserData[]>([]);
  const [pendingRequestsCount, setPendingRequestsCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [viewingEmployeeId, setViewingEmployeeId] = useState<string | null>(null);
  const [allLeaveRequests, setAllLeaveRequests] = useState<LeaveRequest[]>([]);
  const [selectedRequest, setSelectedRequest] = useState<LeaveRequest | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      const [allUsers, allRequests] = await Promise.all([
        usersAPI.getAll(),
        leaveRequestsAPI.getAll()
      ]);

      const employeeUsers = allUsers.filter(u => u.role === 'EMPLOYEE');
      const pendingRequests = allRequests.filter(r => r.status === LeaveStatus.PENDING);

      setEmployees(employeeUsers);
      setAllLeaveRequests(allRequests);
      setPendingRequestsCount(pendingRequests.length);
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <Loading className="h-screen" />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card 
            className="shadow-sm hover:shadow-md transition-shadow duration-200 cursor-pointer"
            onClick={() => setLocation('/admin/employees')}
          >
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-3 bg-primary/10 rounded-full">
                  <i className="fas fa-users text-primary text-xl"></i>
                </div>
                <div className="ml-4">
                  <h3 className="font-semibold text-gray-800">‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô</h3>
                  <p className="text-sm text-gray-600">‡πÄ‡∏û‡∏¥‡πà‡∏° ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç ‡∏•‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-3 bg-green-100 rounded-full">
                  <i className="fas fa-calendar-check text-green-600 text-xl"></i>
                </div>
                <div className="ml-4">
                  <h3 className="font-semibold text-gray-800">‡∏Ñ‡∏≥‡∏Ç‡∏≠‡∏£‡∏≠‡∏û‡∏¥‡∏à‡∏≤‡∏£‡∏ì‡∏≤</h3>
                  <p className="text-2xl font-bold text-green-600">{pendingRequestsCount}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-3 bg-blue-100 rounded-full">
                  <i className="fas fa-chart-line text-blue-600 text-xl"></i>
                </div>
                <div className="ml-4">
                  <h3 className="font-semibold text-gray-800">‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</h3>
                  <p className="text-2xl font-bold text-blue-600">{employees.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Employee Quick Access */}
        <Card className="shadow-sm mb-8">
          <CardContent className="p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-6">‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {employees.length === 0 ? (
                <div className="col-span-full text-center py-8 text-gray-500">
                  ‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö
                </div>
              ) : (
                employees.map((employee) => (
                  <EmployeeCard 
                    key={employee.id} 
                    employee={employee}
                    onClick={() => setViewingEmployeeId(employee.id)}
                  />
                ))
              )}
            </div>
          </CardContent>
        </Card>

        {/* Leave Requests Management */}
        <Tabs defaultValue="list" className="space-y-4">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="list">‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏Ç‡∏≠</TabsTrigger>
            <TabsTrigger value="calendar">‡∏õ‡∏è‡∏¥‡∏ó‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏≤</TabsTrigger>
          </TabsList>

          <TabsContent value="list" className="space-y-4">
            <AllLeaveRequests />
          </TabsContent>

          <TabsContent value="calendar" className="space-y-4">
            <CalendarView 
              leaveRequests={allLeaveRequests}
              isAdmin={true}
              onEventClick={(event) => {
                setSelectedRequest(event.resource);
                setIsModalOpen(true);
              }}
            />
          </TabsContent>
        </Tabs>
      </div>

      {/* Employee Leave View */}
      <EmployeeLeaveView
        employeeId={viewingEmployeeId}
        onClose={() => setViewingEmployeeId(null)}
      />

      {/* Leave Request Modal */}
      <LeaveRequestModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedRequest(null);
        }}
        request={selectedRequest}
        employee={selectedRequest ? employees.find(emp => emp.id === selectedRequest.userId) : null}
        showActions={true}
        onApprove={() => {
          setIsModalOpen(false);
          setSelectedRequest(null);
          loadDashboardData();
        }}
        onReject={() => {
          setIsModalOpen(false);
          setSelectedRequest(null);
          loadDashboardData();
        }}
        onDelete={() => {
          setIsModalOpen(false);
          setSelectedRequest(null);
          loadDashboardData();
        }}
      />
    </div>
  );
};

