// Helper functions for employee creation
const generateUsername = (firstName: string, lastName: string): string => {
  return `${firstName.toLowerCase()}.${lastName.toLowerCase()}`;
};

const getDefaultLeaveBalances = () => ({
  accumulated: 0,
