  start: Date;
  end: Date;
  resource: LeaveRequest;
  status: LeaveStatus;
}

interface CalendarViewProps {
  leaveRequests: LeaveRequest[];
  isAdmin?: boolean;
  onEventClick?: (event: CalendarEvent) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ 
