      id: request.id,
      title: isAdmin 
        ? `${request.employeeName} - ${request.leaveType}`
        : request.leaveType,
      start: new Date(request.startDate),
      end: new Date(request.endDate),
      resource: request,
      status: request.status as LeaveStatus,
