import { LeaveRequest, LeaveStatus } from '@/types';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { formatDateThai } from '@/utils/dateHelpers';
import { generatePrintableLeaveForm } from '@/utils/pdfGenerator';
import { leaveRequestsAPI } from '@/services/api';

interface LeaveRequestModalProps {
  isOpen: boolean;
  onClose: () => void;
  request: LeaveRequest | null;
  employee?: any;
  showActions?: boolean;
  onApprove?: (id: string) => void;
  onReject?: (id: string) => void;
  onDelete?: (id: string) => void;
}

export const LeaveRequestModal: React.FC<LeaveRequestModalProps> = ({
  isOpen,
  onClose,
  request,
  employee,
  showActions = false,
  onApprove,
  onReject,
  onDelete
}) => {
  if (!request) return null;

  const getStatusBadge = (status: LeaveStatus) => {
    switch (status) {
      case LeaveStatus.PENDING:
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">‡∏£‡∏≠‡∏û‡∏¥‡∏à‡∏≤‡∏£‡∏ì‡∏≤</Badge>;
      case LeaveStatus.APPROVED:
        return <Badge variant="secondary" className="bg-green-100 text-green-800">‡∏≠‡∏ô‡∏∏‡∏°‡∏±‡∏ï‡∏¥</Badge>;
      case LeaveStatus.REJECTED:
        return <Badge variant="secondary" className="bg-red-100 text-red-800">‡∏õ‡∏è‡∏¥‡πÄ‡∏™‡∏ò</Badge>;
      default:
        return <Badge variant="secondary">‡πÑ‡∏°‡πà‡∏ó‡∏£‡∏≤‡∏ö‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞</Badge>;
    }
  };

  const handlePrint = () => {
    if (employee) {
      generatePrintableLeaveForm(request, employee);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl !bg-white !border !border-gray-200 !shadow-lg dialog-content" style={{ backgroundColor: 'white', color: '#1f2937' }}>
        <DialogHeader>
          <DialogTitle className="text-xl font-semibold text-gray-900">
            ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Å‡∏≤‡∏£‡∏•‡∏≤
          </DialogTitle>
          <DialogDescription className="text-sm text-gray-600">
            ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Ç‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏Ç‡∏≠‡∏•‡∏≤
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 text-gray-900">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡∏Ç‡∏≠‡∏•‡∏≤</label>
              <p className="text-gray-900">{request.employeeName}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Å‡∏≤‡∏£‡∏•‡∏≤</label>
              <p className="text-gray-900">{request.leaveType}</p>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏•‡∏≤</label>
              <p className="text-gray-900">{formatDateThai(request.startDate)}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î‡∏•‡∏≤</label>
              <p className="text-gray-900">{formatDateThai(request.endDate)}</p>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ß‡∏±‡∏ô</label>
              <p className="text-gray-900">{request.totalDays} ‡∏ß‡∏±‡∏ô</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞</label>
              <div>{getStatusBadge(request.status as LeaveStatus)}</div>
            </div>
          </div>

          <div>
            <label className="text-sm font-medium text-gray-700">‡πÄ‡∏´‡∏ï‡∏∏‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏•‡∏≤</label>
            <p className="text-gray-900 bg-gray-50 p-3 rounded-md">{request.reason}</p>
          </div>

          <div>
            <label className="text-sm font-medium text-gray-700">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</label>
            <p className="text-gray-900">{request.contactNumber}</p>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-gray-700">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏¢‡∏∑‡πà‡∏ô‡∏Ñ‡∏≥‡∏Ç‡∏≠</label>
              <p className="text-gray-900">{formatDateThai(request.requestDate)}</p>
            </div>
            {request.approvedDate && (
              <div>
                <label className="text-sm font-medium text-gray-700">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏≠‡∏ô‡∏∏‡∏°‡∏±‡∏ï‡∏¥</label>
                <p className="text-gray-900">{formatDateThai(request.approvedDate)}</p>
              </div>
            )}
          </div>

          <div className="flex justify-between pt-4 border-t">
            <div className="space-x-2">
              {request.status === LeaveStatus.APPROVED && employee && (
                <Button
                  variant="outline"
                  onClick={handlePrint}
                  className="text-blue-600 hover:text-blue-800"
                >
                  <i className="fas fa-print mr-2"></i>
                  ‡∏û‡∏¥‡∏°‡∏û‡πå‡πÉ‡∏ö‡∏•‡∏≤
                </Button>
              )}
            </div>

            {showActions && (
              <div className="space-x-2">
                {request.status === LeaveStatus.PENDING && (
                  <>
                    <Button
                      variant="outline"
                      onClick={async () => {
                        try {
                          await leaveRequestsAPI.update(request.id, {
                            status: LeaveStatus.REJECTED,
                            approvedDate: new Date()
                          });
                          onReject?.(request.id);
                        } catch (error) {
                          console.error('Error rejecting request:', error);
                        }
                      }}
                      className="text-red-600 hover:text-red-800"
                    >
                      ‡∏õ‡∏è‡∏¥‡πÄ‡∏™‡∏ò
                    </Button>
                    <Button
                      onClick={async () => {
                        try {
                          await leaveRequestsAPI.update(request.id, {
                            status: LeaveStatus.APPROVED,
                            approvedDate: new Date()
                          });
                          onApprove?.(request.id);
                        } catch (error) {
                          console.error('Error approving request:', error);
                        }
                      }}
                      className="bg-green-600 hover:bg-green-700"
                    >
                      ‡∏≠‡∏ô‡∏∏‡∏°‡∏±‡∏ï‡∏¥
                    </Button>
                  </>
                )}
                {(request.status === LeaveStatus.PENDING || request.status === LeaveStatus.REJECTED) && (
                  <Button
                    variant="outline"
                    onClick={() => onDelete?.(request.id)}
                    className="text-red-600 hover:text-red-800"
                  >
                    <i className="fas fa-trash mr-2"></i>
                    ‡∏•‡∏ö
                  </Button>
                )}
              </div>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

